import React,{useState} from "react"
import {Link} from "react-router-dom"
import   "./Join.css"


const Join=()=>{
//-------state variables------------------------//
const [name,setName]=useState("");
const [room,setRoom]=useState("");

//-------the other stuff------------------------//
//two inputs for the username and the chatroom//
//a link that leads to the chatroom if the inputs check out//

    return(
<div className="join-outer-container">
    <div className="join-innner-container">
        <h1>Join</h1>
        <div><input placeholder="Name" className="joinInput" type="text" onChange={(e)=>setName(e.target.value)}></input></div>
        <div><input placeholder="Room" className="joinInput mt-20" type="text" onChange={(e)=>setRoom(e.target.value)}></input></div>
            <Link  onClickt={e=>(!name ||!room)? e.preventDefault():null}to={`/chat?name=${name}&room=${room}`}>
            <button className="button mt-20" type="submit">Sign In</button>
            </Link>

    </div>
</div>


    )
}
export default Join;
