import React,{useState,useEffect} from "react"
import queryString from "query-string"
import io from "socket.io-client"
import   "./Chat.css"


let socket;

const Chat=({location})=>{
    const [name,setName]=useState("");
    const [room,setRoom]=useState("");
    const ENDPOINT="localhost:5000"

useEffect(()=>{
    //fetching the information from the link to get room and name
    //using destructured parameters
   // const {name,room}=queryString.parse(location.search)
    const name=queryString.parse(location.search).name
    const room=queryString.parse(location.search).room
    //line above is equivalent to const name=queryString.parse(location.search).name
    //                                       queryString.parse(location.search).room

    console.log(location.search)
    socket=io(ENDPOINT)

   setName(name)
   setRoom(room)


   socket.emit("join",{name,room},({error})=>{
    alert(error)
   })
   console.log(socket)
},[ENDPOINT,location.search])


    return(
<h1>Chat Page</h1>


    )
}
export default Chat;
